#!/bin/bash

sleep 1m

WORK_DIR=`pwd`

cat >>/home/ubuntu/.ssh/authorized_keys <<EOF
ssh-rsa  nfedotov@cisco.com
EOF

echo "apt_preserve_sources_list: true" | sudo tee /etc/cloud/cloud.cfg.d/99-local-mirror-only.cfg
sudo cp sources.list /etc/apt/
